class Demo4 {
  static get inputProperties() { return ['--rays']; }

  static getDistAlongSide(max) {
    return Math.floor(Math.random() * (max + 1));
  }

  static getWidth() {
    return Math.floor(Math.random() * 15) + 1;
  }

  static getPoint(side, width, height) {
    switch (side) {
      case 0:
        return [
          0,
          Demo4.getDistAlongSide(height),
        ];
      case 1:
        return [
          Demo4.getDistAlongSide(width),
          height,
        ];
      case 2:
        return [
          width,
          Demo4.getDistAlongSide(height),
        ];
      case 3:
        return [
          Demo4.getDistAlongSide(width),
          0,
        ];
      default: return undefined;
    }
  }

  static getEndSide() {
    return Math.floor(Math.random() * 3) + 1;
  }

  static getColor(base, range) {
    return base + Math.floor(Math.random() * range + 1);
  }

  paint(ctx, { width, height }, props) { // eslint-disable-line
    const rays = props.get('--rays');

    ctx.fillStyle = 'rgb(160, 170, 210)';
    ctx.fillRect(0, 0, width, height);

    const ctr = [
      width / 2,
      height / 2,
    ];

    for (let i = 0; i < rays; i += 1) {
      const startSide = i % 4;
      // const endSide = (startSide + Demo4.getEndSide()) % 4;
      ctx.beginPath();
      ctx.moveTo(...ctr);
      ctx.lineTo(...Demo4.getPoint(startSide, width, height));
      ctx.lineWidth = Demo4.getWidth();
      ctx.lineCap = 'square';
      ctx.strokeStyle = `rgba(${Demo4.getColor(130, 150)}, ${Demo4.getColor(110, 120)}, ${Demo4.getColor(35, 150)}, .07)`;
      ctx.stroke();
    }
  }
}
registerPaint('demo4', Demo4); // eslint-disable-line
